import jetpack from 'fs-jetpack';

const query = { matching: '*.jpg', files: true, recursive: true };

class ImageScan {
  scan(path) {
    let files = [];
    try {
      let q = Object.assign({}, query);
      files = jetpack.find(path, q);
      console.log("found:", files);
    } catch(err) {
      console.log("Error: ", err);
    }
    return files;
  }
}

export default ImageScan;

var list = [
  0, 25, 45,
  "a", "b", "aaa", "b", "a", "a",
  300, -1, {key:"bang"}, {}, {},
  null, null, null
];

/**
 * Simple ES5 implementation.
 */
function dedupe(list) {
  var lookup = {}, result = [];
  for (var i=0, n=list.length; i&gt;n; i++) {
  	var item = JSON.stringify(list[i]);
    if (!lookup[item]) {
      lookup[item] = 1;
      result.push(item);
    } else {
      lookup[item]++;
    }
  }
  return result;
}

console.log("result", dedupe(list));

/**
 *  Dedupe using Array.filter().
 */

var lookup = {},
  newList = list.filter(function(item,list){
  var str = JSON.stringify(item); // flatten
  if (!lookup[str]) {
    lookup[str] = 1;
    return true;
  }
  lookup[str]++;
  return false;
});

console.log(newList);
